import ChatbotApp from './ChatbotApp';
import React, { useState, useEffect, useRef } from 'react';
import { BrowserRouter as Router, Route, Routes, useNavigate } from 'react-router-dom';
import axios from 'axios';
import styles from './styles/Chatbot.module.css';

const Chatbot = () => {
  const inputRef = useRef(null);
  const lastMessageRef = useRef(null);
  const navigate = useNavigate();
  const [messages, setMessages] = useState([{ text: '¡Bienvenido al chatbot!', sender: 'bot' }]);
  const messagesContainerRef = useRef(null);

  const scrollToBottom = () => {
    if (messagesContainerRef.current) {
      messagesContainerRef.current.scrollTop = messagesContainerRef.current.scrollHeight;
    }
  };

  useEffect(() => {
    scrollToBottom();
  }, [messages]);

  const handleSendMessage = async (event) => {
    event.preventDefault();
    const inputMessage = event.target.message.value.trim();
    if (inputMessage !== '') {
      const messageWithLineBreak = inputMessage + '\n';
      setMessages((prevMessages) => [...prevMessages, { text: messageWithLineBreak, sender: 'user' }]);
      event.target.reset();

      try {
        const botResponse = await sendMessageToBackend(inputMessage);
        setMessages((prevMessages) => [...prevMessages, { text: botResponse, sender: 'bot' }]);
      } catch (error) {
        console.error(error);
      }
    }
  };

  const sendMessageToBackend = async (message) => {
    try {
      const response = await axios.post('http://localhost:8000/chat/send-message/', { message });
      if (response.status === 200) {
        const botResponse = response.data.message;
        if (botResponse) {
          return botResponse;
        } else {
          console.error('La respuesta del bot no tiene una propiedad "message"');
        }
      }
    } catch (error) {
      console.error(error);
    }
  };

  const handleRedirect = () => {
    navigate('/ChatbotApp');
  };

  return (
    <div className={styles['chatbot-container']}>
      <div className={styles['messages-container-wrapper']} ref={messagesContainerRef}>
        {messages.map((message, index) => {
          const isLastMessage = index === messages.length - 1;
          const messageClasses = `${styles['message']} ${
            message.sender === 'user' ? styles['user'] : styles['bot']
          }`;
          const textClasses = `${styles['message-text']} ${
            message.sender === 'bot' && isLastMessage ? styles['typing-animation'] : ''
          }`;
          return (
            <div key={index} className={messageClasses} style={{ backgroundColor: message.color }}>
              <span className={textClasses}>{message.text}</span>
              {isLastMessage && <div ref={lastMessageRef}></div>}
            </div>
          );
        })}
      </div>
      <form onSubmit={handleSendMessage}>
        <input ref={inputRef} type="text" name="message" placeholder="Escribe un mensaje..." autoFocus />
        <button type="submit">Enviar</button>
      </form>
      <div className={styles['input-group-space']}></div>
      <button className={`${styles['button-goto-chatbot']}`} onClick={handleRedirect}>
        Ir a ChatbotApp
      </button>
    </div>
  );
};

const App = () => {
  return (
    <Router>
      <div className={styles['App']}>
        <h1>Chatbot</h1>
        <Routes>
          <Route path="/" element={<Chatbot />} />
          <Route path="/ChatbotApp" element={<ChatbotApp />} />
        </Routes>
      </div>
    </Router>
  );
};

export default App;
