import ChatbotApp from './ChatbotApp';
import React, { useState, useEffect, useRef } from 'react';
import { BrowserRouter as Router, Route, Routes, useNavigate } from 'react-router-dom';
import axios from 'axios';
import styles from './styles/Chatbot.module.css';

const TypingText = ({ text, onTypingComplete }) => {
  const [displayText, setDisplayText] = useState('');

  useEffect(() => {
    let currentIndex = 0;
    const typingTimer = setInterval(() => {
      setDisplayText((prevText) => {
        if (currentIndex >= text.length) {
          clearInterval(typingTimer);
          onTypingComplete();
          return prevText;
        }
        currentIndex++;
        return text.substring(0, currentIndex);
      });
    }, 100);

    return () => {
      clearInterval(typingTimer);
    };
  }, [text, onTypingComplete]);

  useEffect(() => {
    onTypingComplete();
  }, [onTypingComplete]);

  return <span>{displayText}</span>;
};

const Chatbot = () => {
  const navigate = useNavigate();
  const [messages, setMessages] = useState([{ text: '¡Bienvenido al chatbot!', sender: 'bot' }]);
  const [isBotWriting, setIsBotWriting] = useState(false);
  const messagesContainerRef = useRef(null);

  const scrollToBottom = () => {
    if (messagesContainerRef.current) {
      messagesContainerRef.current.scrollTop = messagesContainerRef.current.scrollHeight;
    }
  };

  useEffect(() => {
    scrollToBottom();
  }, [messages]);

  const handleSendMessage = async (event) => {
    event.preventDefault();
    const inputMessage = event.target.message.value.trim();
    if (inputMessage !== '') {
      const messageWithLineBreak = inputMessage + '\n';
      setMessages((prevMessages) => [...prevMessages, { text: messageWithLineBreak, sender: 'user' }]);
      event.target.reset();
      setIsBotWriting(true);

      try {
        const botResponse = await sendMessageToBackend(inputMessage);
        await wait(1000);

        setMessages((prevMessages) => [...prevMessages, { text: botResponse, sender: 'bot' }]);
      } catch (error) {
        console.error(error);
      }

      setIsBotWriting(false);
      event.target.message.focus();
    }
  };

  const sendMessageToBackend = async (message) => {
    try {
      const response = await axios.post('http://localhost:8000/chat/send-message/', { message });
      if (response.status === 200) {
        const botResponse = response.data.message;
        if (botResponse) {
          return botResponse;
        } else {
          console.error('La respuesta del bot no tiene una propiedad "message"');
        }
      }
    } catch (error) {
      console.error(error);
    }
  };

  const wait = (ms) => {
    return new Promise((resolve) => setTimeout(resolve, ms));
  };

  const handleRedirect = () => {
    navigate('/ChatbotApp');
  };

  return (
    <div className={styles['chatbot-container']}>
      <div id="messages-container" className={styles['messages-container']} ref={messagesContainerRef}>
        {messages.map((message, index) => (
          <div
            key={index}
            className={`${styles['message']} ${message.sender === 'user' ? styles['user'] : styles['bot']}`}
            style={{ backgroundColor: message.color }}
          >
            {message.sender === 'bot' ? (
              <>
                {index === messages.length - 1 && isBotWriting ? (
                  <TypingText text={message.text} onTypingComplete={scrollToBottom} />
                ) : (
                  <span className={styles['message-text']}>{message.text}</span>
                )}
              </>
            ) : (
              <span className={styles['message-text']}>{message.text}</span>
            )}
          </div>
        ))}
      </div>
      <form onSubmit={handleSendMessage}>
        <input
          type="text"
          name="message"
          placeholder="Escribe un mensaje..."
          disabled={isBotWriting}
        />
        <button type="submit" disabled={isBotWriting}>Enviar</button>
      </form>
      <button onClick={handleRedirect}>Ir a ChatbotApp</button>
    </div>
  );
};

const App = () => {
  return (
    <Router>
      <div className={styles['App']}>
        <h1>Chatbot</h1>
        <Routes>
          <Route path="/" element={<Chatbot />} />
          <Route path="/ChatbotApp" element={<ChatbotApp />} />
        </Routes>
      </div>
    </Router>
  );
};

export default App;
