import ChatbotApp from './ChatbotApp';
import React, { useState, useEffect, useRef } from 'react';
import { BrowserRouter as Router, Route, Routes, useNavigate } from 'react-router-dom';
import styles from './styles/Chatbot.module.css';
import axios from 'axios';

const TypingText = ({ text, onTypingComplete }) => {
  const [displayText, setDisplayText] = useState('');

  useEffect(() => {
    let currentIndex = 0;
    const typingTimer = setInterval(() => {
      setDisplayText((prevText) => {
        if (currentIndex >= text.length) {
          clearInterval(typingTimer);
          onTypingComplete(); // Invocar la función cuando el texto de escritura haya terminado
          return prevText;
        }
        currentIndex++;
        return text.substring(0, currentIndex);
      });
    }, 100);

    return () => {
      clearInterval(typingTimer);
    };
  }, [text, onTypingComplete]);

  return <>{displayText}</>;
};

const Chatbot = () => {
  const navigate = useNavigate();

  const [messages, setMessages] = useState([
    { text: '¡Bienvenido al chatbot!', sender: 'bot' },
  ]);
  const [isBotTyping, setIsBotTyping] = useState(false); // Estado para controlar si el bot está escribiendo
  const [isBotWriting, setIsBotWriting] = useState(false); // Estado para controlar si el bot está escribiendo un mensaje

  const messagesContainerRef = useRef(null);
const scrollToBottom = () => {
  if (messagesContainerRef.current) {
    messagesContainerRef.current.scrollTop = messagesContainerRef.current.scrollHeight;
  }
};

const handleSendMessage = async (event) => {
  event.preventDefault();
  const inputMessage = event.target.message.value;
  if (inputMessage.trim() !== '') {
    const messageWithLineBreak = inputMessage + '\n';
    setMessages([...messages, { text: messageWithLineBreak, sender: 'user' }]);
    event.target.reset();

    setIsBotTyping(true);
    setIsBotWriting(true);

    try {
      const botResponse = await sendMessageToBackend(inputMessage);
      await wait(1000);

      setMessages((prevMessages) => [
        ...prevMessages,
        { text: botResponse, sender: 'bot' },
      ]);

      // Desplazamiento automático hacia abajo
      scrollToBottom();
    } catch (error) {
      console.error(error);
    }

    setIsBotTyping(false);
    setIsBotWriting(false);

    // Enfoque automático en el campo de entrada
    event.target.message.focus();
  }
};

useEffect(() => {
  const scrollToBottom = () => {
    if (messagesContainerRef.current) {
      messagesContainerRef.current.scrollTop = messagesContainerRef.current.scrollHeight;
    }
  };

  const handleNewLine = (event) => {
    if (event.key === 'Enter' && !event.shiftKey) {
      // Desplazamiento automático hacia abajo al saltar de línea
      scrollToBottom();
    }
  };

  window.addEventListener('keydown', handleNewLine);

  return () => {
    window.removeEventListener('keydown', handleNewLine);
  };
}, []);

  useEffect(() => {
    // Función para desplazarse hacia abajo al finalizar la carga de mensajes
  // Desplazarse hacia abajo al cargar el componente y cuando se actualicen los mensajes
  const scrollToBottom = () => {
    if (messagesContainerRef.current) {
      messagesContainerRef.current.scrollTop = messagesContainerRef.current.scrollHeight;
    }
  };


    // Desplazarse hacia abajo al cargar el componente y cuando se actualicen los mensajes
    scrollToBottom();
  }, [messages]);

  const sendMessageToBackend = async (message) => {
    try {
      const response = await axios.post('http://localhost:8000/chat/send-message/', {
        message: message,
      });

      if (response.status === 200) {
        const botResponse = response.data.message;
        if (botResponse) {
          return botResponse;
        } else {
          console.error('La respuesta del bot no tiene una propiedad "message"');
        }
      }
    } catch (error) {
      console.error(error);
    }
  };

  const wait = (ms) => {
    return new Promise((resolve) => setTimeout(resolve, ms));
  };

  const handleRedirect = () => {
    navigate('/ChatbotApp');
  };

  return (
    <div className={styles['chatbot-container']}>
      <div id="messages-container" className={styles['messages-container']}>
        {messages.map((message, index) => (
          <div
            key={index}
            className={`${styles['message']} ${
              message.sender === 'user' ? styles['user'] : styles['bot']
            }`}
            style={{ backgroundColor: message.color }}
          >
            {message.sender === 'bot' ? (
              <>
                {index === messages.length - 1 ? (
                  <TypingText
                    text={message.text}
                    onTypingComplete={() => setIsBotWriting(false)}
                  />
                ) : (
                  <span className={styles['message-text']}>{message.text}</span>
                )}
              </>
            ) : (
              <span className={styles['message-text']}>{message.text}</span>
            )}
          </div>
        ))}
        <div ref={messagesContainerRef} />
      </div>
      <form onSubmit={handleSendMessage}>
      <input
        type="text"
        name="message"
        placeholder="Escribe un mensaje..."
        disabled={isBotTyping || isBotWriting}
        autoFocus // Enfoque automático en el campo de entrada
      />
        <button type="submit" disabled={isBotTyping || isBotWriting}>
          Enviar
        </button>{' '}
        {/* Deshabilitar el botón de enviar mientras el bot está escribiendo o escribiendo un mensaje */}
      </form>
      <button onClick={handleRedirect}>Ir a ChatbotApp</button>
    </div>
  );
};

const App = () => {
  return (
    <Router>
      <div className={styles['App']}>
        <h1>Chatbot</h1>
        <Routes>
          <Route path="/" element={<Chatbot />} />
          <Route path="/ChatbotApp" element={<ChatbotApp />} />
        </Routes>
      </div>
    </Router>
  );
};

export default App;
