import React, { useState, useEffect, useRef } from 'react';
import { BrowserRouter as Router, Route, Routes, useNavigate } from 'react-router-dom';
import axios from 'axios';
import styles from './styles/Chatbot.module.css';

const Chatbot = () => {
  const navigate = useNavigate();
  const [userMessages, setUserMessages] = useState([]);
  const [botMessages, setBotMessages] = useState([]);
  const [isBotWriting, setIsBotWriting] = useState(false);
  const messagesContainerRef = useRef(null);

  const scrollToBottom = () => {
    if (messagesContainerRef.current) {
      messagesContainerRef.current.scrollTop = messagesContainerRef.current.scrollHeight;
    }
  };

  useEffect(() => {
    scrollToBottom();
  }, [userMessages, botMessages]);

  const handleSendMessage = async (event) => {
    event.preventDefault();
    const inputMessage = event.target.message.value.trim();
    if (inputMessage !== '') {
      const messageWithLineBreak = inputMessage + '\n';
      setUserMessages((prevUserMessages) => [
        ...prevUserMessages,
        { text: messageWithLineBreak, sender: 'user' },
      ]);
      event.target.reset();
      setIsBotWriting(true);

      try {
        const botResponse = await sendMessageToBackend(inputMessage);

        // Mostrar el efecto de escritura letra por letra solo para el mensaje del bot
        let responseSubstring = '';
        /* eslint-disable no-loop-func */
        for (let i = 0; i < botResponse.length; i++) {
          await wait(100); // Esperar un tiempo entre cada letra (ajusta el tiempo según tus preferencias)
          responseSubstring += botResponse.charAt(i);
          if (i === botResponse.length - 1) {
            setBotMessages((prevBotMessages) => [
              ...prevBotMessages,
              { text: responseSubstring, sender: 'bot' },
            ]);
          } else {
            setBotMessages((prevBotMessages) => [
              ...prevBotMessages,
              { text: responseSubstring, sender: 'bot' },
            ]);
          }
          scrollToBottom();
        }
        /* eslint-enable no-loop-func */
      } catch (error) {
        console.error(error);
      }

      setIsBotWriting(false);
      event.target.message.focus();
    }
  };

  const sendMessageToBackend = async (message) => {
    try {
      const response = await axios.post('http://localhost:8000/chat/send-message/', { message });
      if (response.status === 200) {
        const botResponse = response.data.message;
        if (botResponse) {
          return botResponse;
        } else {
          console.error('La respuesta del bot no tiene una propiedad "message"');
        }
      }
    } catch (error) {
      console.error(error);
    }
  };

  const wait = (ms) => {
    return new Promise((resolve) => setTimeout(resolve, ms));
  };

  const handleRedirect = () => {
    navigate('/ChatbotApp');
  };

  return (
    <div className={styles['chatbot-container']}>
      <div id="messages-container" className={styles['messages-container']} ref={messagesContainerRef}>
        {userMessages.map((message, index) => (
          <div
            key={index}
            className={`${styles['message']} ${styles['user']}`}
            style={{ backgroundColor: message.color }}
          >
            <span className={styles['message-text']}>{message.text}</span>
          </div>
        ))}
        {botMessages.map((message, index) => (
          <div
            key={index}
            className={`${styles['message']} ${styles['bot']}`}
            style={{ backgroundColor: message.color }}
          >
            <span className={styles['message-text']}>{message.text}</span>
          </div>
        ))}
      </div>
      <form onSubmit={handleSendMessage}>
        <input
          type="text"
          name="message"
          placeholder="Escribe un mensaje..."
          disabled={isBotWriting}
        />
        <button type="submit" disabled={isBotWriting}>Enviar</button>
      </form>
      <div className={styles['input-group-space']}></div> {/* Espacio adicional */}
      <button className={styles['button-goto-chatbot']} onClick={handleRedirect}>
        Ir a ChatbotApp
      </button>
    </div>
  );
};

const ChatbotApp = () => {
  return (
    <div>
      {/* Contenido de ChatbotApp */}
    </div>
  );
};

const App = () => {
  return (
    <Router>
      <div className={styles['App']}>
        <h1>Chatbot</h1>
        <Routes>
          <Route path="/" element={<Chatbot />} />
          <Route path="/ChatbotApp" element={<ChatbotApp />} />
        </Routes>
      </div>
    </Router>
  );
};

export default App;
