import ChatbotApp from './ChatbotApp';
import React, { useState, useEffect, useRef } from 'react';
import { BrowserRouter as Router, Route, Routes, useNavigate } from 'react-router-dom';
import axios from 'axios';
import styles from './styles/Chatbot.module.css';

const Chatbot = () => {
  const inputRef = useRef(null);
  const lastMessageRef = useRef(null);
  const navigate = useNavigate();
  const [messages, setMessages] = useState([{ text: '¡Bienvenido al chatbot!', sender: 'bot' }]);
  const messagesContainerRef = useRef(null);

  const scrollToBottom = () => {
    if (lastMessageRef.current) {
      lastMessageRef.current.scrollIntoView({ behavior: 'smooth' });
    }
  };

  useEffect(() => {
    scrollToBottom();
  }, [messages]);

  const handleSendMessage = async (event) => {
    event.preventDefault();
    const inputMessage = event.target.message.value.trim();
    if (inputMessage !== '') {
      const messageWithLineBreak = inputMessage + '\n';
      setMessages((prevMessages) => [...prevMessages, { text: messageWithLineBreak, sender: 'user' }]);
      event.target.reset();

      try {
        const botResponse = await sendMessageToBackend(inputMessage);
        setMessages((prevMessages) => [
          ...prevMessages,
          { text: botResponse, sender: 'bot', typing: true },
        ]);
      } catch (error) {
        console.error(error);
      }
    }
  };

  const sendMessageToBackend = async (message) => {
    try {
      const response = await axios.post('http://localhost:8000/chat/send-message/', { message });
      if (response.status === 200) {
        const botResponse = response.data.message;
        if (botResponse) {
          return botResponse;
        } else {
          console.error('La respuesta del bot no tiene una propiedad "message"');
        }
      }
    } catch (error) {
      console.error(error);
    }
  };

  const handleRedirect = () => {
    navigate('/ChatbotApp');
  };

  return (
    <div className={styles['chatbot-container']}>
      <div className={styles['messages-container-wrapper']}>
        <div id="messages-container" className={styles['messages-container']} ref={messagesContainerRef}>
          {messages.map((message, index) => (
            <div
              key={index}
              className={`${styles['message']} ${message.sender === 'user' ? styles['user'] : styles['bot']}`}
              style={{ backgroundColor: message.color }}
              ref={index === messages.length - 1 ? lastMessageRef : null}
            >
              <span className={styles['message-text']}>
                {message.sender === 'bot' && message.typing ? (
                  <TypingEffect
                    text={message.text}
                    typingDelay={100}
                    erasingDelay={50}
                    pauseDelay={1500}
                    onTypingComplete={() => {
                      setMessages((prevMessages) => {
                        const updatedMessages = [...prevMessages];
                        updatedMessages[index].typing = false;
                        return updatedMessages;
                      });
                    }}
                  />
                ) : (
                  message.text
                )}
              </span>
            </div>
          ))}
          <div ref={lastMessageRef}></div>
        </div>
      </div>
      <form onSubmit={handleSendMessage}>
        <input
          ref={inputRef}
          type="text"
          name="message"
          placeholder="Escribe un mensaje..."
          autoFocus
        />
        <button type="submit">
          Enviar
        </button>
      </form>
      <div className={styles['input-group-space']}></div>
      <button className={`${styles['button-goto-chatbot']}`} onClick={handleRedirect}>
        Ir a ChatbotApp
      </button>
    </div>
  );
};

const TypingEffect = ({ text, typingDelay, erasingDelay, pauseDelay, onTypingComplete }) => {
  const [displayText, setDisplayText] = useState('');

  useEffect(() => {
    const type = async () => {
      for (let i = 0; i < text.length; i++) {
        await sleep(typingDelay);
        setDisplayText((prevDisplayText) => prevDisplayText + text.charAt(i));
      }
      await sleep(pauseDelay);
      erase();
    };

    const erase = async () => {
      for (let i = text.length - 1; i >= 0; i--) {
        await sleep(erasingDelay);
        setDisplayText((prevDisplayText) => prevDisplayText.slice(0, -1));
      }
      onTypingComplete();
    };

    const sleep = (duration) => new Promise((resolve) => setTimeout(resolve, duration));

    type();
  }, [text, typingDelay, erasingDelay, pauseDelay, onTypingComplete]);

  return <>{displayText}</>;
};

const App = () => {
  return (
    <Router>
      <div className={styles['App']}>
        <h1>Chatbot</h1>
        <Routes>
          <Route path="/" element={<Chatbot />} />
          <Route path="/ChatbotApp" element={<ChatbotApp />} />
        </Routes>
      </div>
    </Router>
  );
};

export default App;
