import ChatbotApp from './ChatbotApp';
import React, { useState, useEffect, useRef } from 'react';
import { BrowserRouter as Router, Route, Routes, useNavigate } from 'react-router-dom';
import styles from './styles/Chatbot.module.css';
import axios from 'axios';

const TypingText = ({ text }) => {
  const [displayText, setDisplayText] = useState('');

  useEffect(() => {
    let currentIndex = 0;
    const typingTimer = setInterval(() => {
      setDisplayText((prevText) => {
        if (currentIndex >= text.length) {
          clearInterval(typingTimer);
          return prevText;
        }
        currentIndex++;
        return text.substring(0, currentIndex);
      });
    }, 100);

    return () => {
      clearInterval(typingTimer);
    };
  }, [text]);

  return <>{displayText}</>;
};

const Chatbot = () => {
  const navigate = useNavigate();

  const [messages, setMessages] = useState([
    { text: '¡Bienvenido al chatbot!', sender: 'bot' },
  ]);

  const messagesContainerRef = useRef(null);

  const handleSendMessage = async (event) => {
    event.preventDefault();
    const inputMessage = event.target.message.value;
    if (inputMessage.trim() !== '') {
      const messageWithLineBreak = inputMessage + '\n';
      setMessages([...messages, { text: messageWithLineBreak, sender: 'user' }]);
      event.target.reset();

      try {
        await sendMessageToBackend(inputMessage);
      } catch (error) {
        console.error(error);
      }
    }
  };

  const sendMessageToBackend = async (message) => {
    try {
      const response = await axios.post('http://localhost:8000/chat/send-message/', {
        message: message,
      });

      if (response.status === 200) {
        const botResponse = response.data.message;
        if (botResponse) {
          setMessages((prevMessages) => [
            ...prevMessages,
            { text: botResponse, sender: 'bot' },
          ]);
        } else {
          console.error('La respuesta del bot no tiene una propiedad "message"');
        }
      }
    } catch (error) {
      console.error(error);
    }
  };

  useEffect(() => {
    if (messagesContainerRef.current) {
      messagesContainerRef.current.scrollIntoView({ behavior: 'smooth', block: 'end' });
    }
  }, [messages]);

  const handleRedirect = () => {
    navigate('/ChatbotApp.js');
  };
  return (
    <div className={styles['chatbot-container']}>
      <div className={styles['messages-container-wrapper']}>
        <div id="messages-container" className={styles['messages-container']} ref={messagesContainerRef}>
          {messages.map((message, index) => (
            <div
              key={index}
              className={`${styles['message']} ${message.sender === 'user' ? styles['user'] : styles['bot']}`}
              style={{ backgroundColor: message.color }}
            >
              {message.sender === 'bot' && (
                <span
                  className={`${styles['message-text']} ${styles['typing-animation']}`}
                  style={{ '--n': message.text.length }}
                >
                  {message.text}
                </span>
              )}
              {message.sender !== 'bot' && (
                <span className={styles['message-text']}>{message.text}</span>
              )}
            </div>
          ))}
        </div>
      </div>
      <form onSubmit={handleSendMessage}>
        <input
          type="text"
          name="message"
          placeholder="Escribe un mensaje..."
          disabled={isBotWriting}
        />
        <button type="submit" disabled={isBotWriting}>Enviar</button>
      </form>
      <div className={styles['input-group-space']}></div>

      <button className={`${styles['button-goto-chatbot']}`} onClick={handleRedirect}>
        Ir a ChatbotApp
      </button>
    </div>
  );
};

const App = () => {
  return (
    <Router>
      <div className={styles['App']}>
        <h1>Chatbot</h1>
        <Routes>
          <Route path="/" element={<Chatbot />} />
          <Route path="/ChatbotApp.js" element={<ChatbotApp />} />
        </Routes>
      </div>
    </Router>
  );
};

export default App;

