import React, { useState } from 'react';
import axios from 'axios';
import { Link, useNavigate } from 'react-router-dom';
import styles from './styles/ChatbotApp.module.css';

const ITEMS_PER_PAGE = 1;
const MAX_VISIBLE_PAGES = 5;

const ChatbotApp = () => {
  const navigate = useNavigate();
  const [tag, setTag] = useState('');
  const [questions, setQuestions] = useState(['']);
  const [responses, setResponses] = useState(['']);
  const [currentPage, setCurrentPage] = useState(1);
  const [message, setMessage] = useState('');

  const handleTagChange = (event) => {
    setTag(event.target.value);
  };

  const handleQuestionChange = (index, event) => {
    const updatedQuestions = [...questions];
    updatedQuestions[index] = event.target.value;
    setQuestions(updatedQuestions);
  };

  const handleResponseChange = (index, event) => {
    const updatedResponses = [...responses];
    updatedResponses[index] = event.target.value;
    setResponses(updatedResponses);
  };

  const handleAddPair = () => {
    setQuestions(['', ...questions]);
    setResponses(['', ...responses]);
  };

  const handleRemovePair = (index) => {
    const updatedQuestions = [...questions];
    updatedQuestions.splice(index, 1);
    setQuestions(updatedQuestions);

    const updatedResponses = [...responses];
    updatedResponses.splice(index, 1);
    setResponses(updatedResponses);

    if (currentPage > 1 && updatedQuestions.length <= (currentPage - 1) * ITEMS_PER_PAGE) {
      setCurrentPage(currentPage - 1);
    }
  };

  const handlePageChange = (page) => {
    setCurrentPage(page);
  };

  const handleSubmit = async (event) => {
    event.preventDefault();

  const handleRedirect = () => {
    navigate('/');
  };  

    const qaPairs = {
      tag: tag,
      patterns: questions,
      responses: responses,
    };

    try {
      const response = await axios.post('http://localhost:8000/chat/add-qa-pairs', qaPairs);

      if (response.status === 200) {
        setMessage('Se han agregado correctamente las preguntas y respuestas al archivo intents.json.');
        setTag('');
        setQuestions(['']);
        setResponses([]);
        setCurrentPage(1);
      } else {
        setMessage('Ha ocurrido un error al agregar las preguntas y respuestas.');
      }
    } catch (error) {
      console.error(error);
      setMessage('Ha ocurrido un error al agregar las preguntas y respuestas.');
    }
  };

  const startIndex = (currentPage - 1) * ITEMS_PER_PAGE;
  const endIndex = startIndex + ITEMS_PER_PAGE;

  const filteredQuestions = questions.slice(startIndex, endIndex);
  const filteredResponses = responses.slice(startIndex, endIndex);

  const formElements = filteredQuestions.map((question, index) => (
    <div className={styles.formGroup} key={index}>
      <div className={styles['input-group']}>
        <div className={styles['form-group']}>
          <label htmlFor="tag-input" style={{ color: '#fff' }}>Etiqueta:</label>
          <div className={styles['tag-input-container']}>
            <input
              type="text"
              id="tag-input"
              name="tag-input"
              value={tag}
              onChange={handleTagChange}
              required
            />
          </div>
        </div>
        <div className={styles['input-group-space']}></div> {/* Espacio adicional */}
        <label htmlFor={`question-${index}`} style={{ color: '#fff' }}>Pregunta:</label>
        <textarea
          id={`question-${index}`}
          name={`question-${index}`}
          value={question}
          onChange={(event) => handleQuestionChange(index + startIndex, event)}
          required
          className={styles['text-field']}
        />
        <div className={styles['input-group-space']}></div> {/* Espacio adicional */}
      </div>
      <div className={styles['input-group']}>
        <label htmlFor={`response-${index}`} style={{ color: '#fff' }}>Respuesta:</label>
        <textarea
          id={`response-${index}`}
          name={`response-${index}`}
          value={filteredResponses[index]}
          onChange={(event) => handleResponseChange(index + startIndex, event)}
          required
          className={styles['text-field']}
        />
        <div className={styles['input-group-space']}></div> {/* Espacio adicional */}
      </div>
      <div className={`${styles['button-container']} ${styles['form-group-container']}`}>
        <button type="button" className={styles['remove-button']} onClick={() => handleRemovePair(index + startIndex)}>Eliminar</button>
      </div>
    </div>
  ));

  const totalPages = Math.ceil(questions.length / ITEMS_PER_PAGE);
  const pageNumbers = Array.from({ length: totalPages }, (_, index) => index + 1);

  let visiblePages = pageNumbers;

  if (totalPages > MAX_VISIBLE_PAGES) {
    const firstVisiblePage = Math.max(1, currentPage - Math.floor(MAX_VISIBLE_PAGES / 2));
    const lastVisiblePage = Math.min(totalPages, firstVisiblePage + MAX_VISIBLE_PAGES - 1);

    visiblePages = pageNumbers.slice(firstVisiblePage - 1, lastVisiblePage);
  }

  return (
    <div className={styles['chatbot-app-container']}>
      <form onSubmit={handleSubmit}>
        <div className={`${styles['formContainer']}`}>
          {formElements}
        </div>
        <div className={styles['submit-button-container']}>
          <button type="button" onClick={handleAddPair}>Agregar Par</button>
          <button type="submit" className={styles['submit-button']}>Enviar</button>
        </div>
      </form>

      <div className={styles['pagination']}>
        {currentPage > 1 && (
          <button
            className={styles['page-button']}
            onClick={() => handlePageChange(currentPage - 1)}
          >
            Ant
          </button>
        )}

        {visiblePages.map((page, index) => (
          <button
            key={index}
            className={page === currentPage ? styles['active-page-button'] : styles['page-button']}
            onClick={() => handlePageChange(page)}
          >
            {page}
          </button>
        ))}

        {currentPage < totalPages && (
          <button
            className={styles['page-button']}
            onClick={() => handlePageChange(currentPage + 1)}
          >
            Sig
          </button>
        )}
      </div>

      {message && <p className={styles['message']}>{message}</p>}
      <Link to="/" className={styles['redirect-button']} onClick={handleRedirect}>Ir a Chatbot</Link>    </div>

  );
};

export default ChatbotApp;
