import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';
import styles from './styles/ChatbotApp.module.css';

const { stringify } = JSON;

const ITEMS_PER_PAGE = 1;
const MAX_VISIBLE_PAGES = 5;

const ChatbotApp = () => {
  const [tag, setTag] = useState('');
  const [questions, setQuestions] = useState(['']);
  const [responses, setResponses] = useState(['']);
  const [currentPage, setCurrentPage] = useState(1);
  const [message, setMessage] = useState('');
  const [processing, setProcessing] = useState(false);
  const formRef = useRef(null); // Referencia al formulario

  const navigate = useNavigate();

  const handleTagChange = (event) => {
    setTag(event.target.value);
  };

  const handleQuestionChange = (index, event) => {
    const updatedQuestions = [...questions];
    updatedQuestions[index] = event.target.value;
    setQuestions(updatedQuestions);
  };

  const handleResponseChange = (index, event) => {
    const updatedResponses = [...responses];
    updatedResponses[index] = event.target.value;
    setResponses(updatedResponses);
  };

  const handleAddPair = () => {
    setQuestions(['', ...questions]);
    setResponses(['', ...responses]);
  };

  const handleRemovePair = (index) => {
    const updatedQuestions = [...questions];
    updatedQuestions.splice(index, 1);
    setQuestions(updatedQuestions);

    const updatedResponses = [...responses];
    updatedResponses.splice(index, 1);
    setResponses(updatedResponses);

    if (currentPage > 1 && updatedQuestions.length <= (currentPage - 1) * ITEMS_PER_PAGE) {
      setCurrentPage(currentPage - 1);
    }
  };

  const handlePageChange = (page) => {
    setCurrentPage(page);
  };

  const handleSubmit = async (event) => {
    event.preventDefault();

    const qaPairs = {
      tag: tag,
      patterns: questions,
      responses: responses,
    };

    try {
      const response = await axios.post('http://localhost:8000/chat/add-qa-pairs/', stringify(qaPairs), {
        headers: {
          'Content-Type': 'application/json',
        },
      });

      if (response.status === 200) {
        setMessage('Se han agregado correctamente las preguntas y respuestas al archivo intents.json.');
        setTag('');
        setQuestions(['']);
        setResponses([]);
        setCurrentPage(1);
        formRef.current.reset(); // Reiniciar los valores del formulario
      } else {
        setMessage('Ha ocurrido un error al agregar las preguntas y respuestas.');
      }
    } catch (error) {
      console.error(error);
      setMessage('Ha ocurrido un error al agregar las preguntas y respuestas.');
    }
  };


  const process_data = async () => {
    try {
      setProcessing(true);
      await axios.post('http://localhost:8000/chat/process-data/');
      navigate('/');
    } catch (error) {
      console.error(error);
    } finally {
      setProcessing(false);
    }
  };

  const startIndex = (currentPage - 1) * ITEMS_PER_PAGE;
  const endIndex = startIndex + ITEMS_PER_PAGE;

  const filteredQuestions = questions.slice(startIndex, endIndex);

  const formElements = filteredQuestions.map((question, index) => {
    const questionIndex = startIndex + index;
    const responseValue = responses[questionIndex];

    return (
      <div className={styles.formGroup} key={questionIndex}>
        <div className={styles['input-group']}>
          <div className={styles['form-group']}>
            <label htmlFor="tag-input" style={{ color: '#fff' }}>Etiqueta:</label>
            <div className={styles['tag-input-container']}>
              <input
                type="text"
                id="tag-input"
                name="tag-input"
                value={tag}
                onChange={handleTagChange}
                required
              />
            </div>
          </div>
          <div className={styles['input-group-space']}></div>
          <label htmlFor={`question-${questionIndex}`} style={{ color: '#fff' }}>Pregunta:</label>
          <textarea
            id={`question-${questionIndex}`}
            name={`question-${questionIndex}`}
            value={question}
            onChange={(event) => handleQuestionChange(questionIndex, event)}
            required
            className={styles['text-field']}
          />
          <div className={styles['input-group-space']}></div>
        </div>
        <div className={styles['input-group']}>
          <label htmlFor={`response-${questionIndex}`} style={{ color: '#fff' }}>Respuesta:</label>
          <textarea
            id={`response-${questionIndex}`}
            name={`response-${questionIndex}`}
            value={responseValue}
            onChange={(event) => handleResponseChange(questionIndex, event)}
            required
            className={styles['text-field']}
          />
          <div className={styles['input-group-space']}></div>
        </div>
        <div className={`${styles['button-container']} ${styles['form-group-container']}`}>
          <button type="button" className={styles['remove-button']} onClick={() => handleRemovePair(questionIndex)}>Eliminar</button>
        </div>
        <div className={styles['button-space']}></div>
      </div>
    );
  });

  const totalPages = Math.ceil(questions.length / ITEMS_PER_PAGE);
  const pageNumbers = Array.from({ length: totalPages }, (_, index) => index + 1);

  let visiblePages = pageNumbers;

  if (totalPages > MAX_VISIBLE_PAGES) {
    const firstVisiblePage = Math.max(1, currentPage - Math.floor(MAX_VISIBLE_PAGES / 2));
    const lastVisiblePage = Math.min(totalPages, firstVisiblePage + MAX_VISIBLE_PAGES - 1);

    visiblePages = pageNumbers.slice(firstVisiblePage - 1, lastVisiblePage);
  }

  const handleRedirect = () => {
    navigate('/');
  };

  return (
    <div className={styles['chatbot-app-container']}>
      <form onSubmit={handleSubmit}>
        <div className={`${styles['formContainer']}`}>
          {formElements}
        </div>
        <div className={styles['submit-button-container']}>
          <button type="button" onClick={handleAddPair}>Agregar Par</button>
          <button type="submit" className={styles['submit-button']}>Enviar</button>
        </div>
      </form>

      <div className={styles['pagination']}>
        {currentPage > 1 && (
          <button
            className={styles['page-button']}
            onClick={() => handlePageChange(currentPage - 1)}
          >
            Ant
          </button>
        )}

        {visiblePages.map((page, index) => (
          <button
            key={index}
            className={page === currentPage ? styles['active-page-button'] : styles['page-button']}
            onClick={() => handlePageChange(page)}
          >
            {page}
          </button>
        ))}

        {currentPage < totalPages && (
          <button
            className={styles['page-button']}
            onClick={() => handlePageChange(currentPage + 1)}
          >
            Sig
          </button>
        )}
      </div>

      {message && <p className={styles['message']}>{message}</p>}
      <div className={styles['input-group-space']}></div>
      <div className={`${styles['button-container']}`}>
        <button
          className={`${styles['redirect-button2']}`}
          onClick={handleRedirect}
        >
          Chatbot App
        </button>
        <button
          className={`${styles['redirect-button']}`}
          onClick={process_data}
          disabled={processing}
        >
          Entrenar red
        </button>
      </div>

      {processing && (
        <div className={styles['overlay']}>
          <div className={styles['loader']}></div>
          <p className={styles['loading-message']}></p>
        </div>
      )}
    </div>
  );
};

export default ChatbotApp;
